mapper.insert:
INSERT INTO ym_robot_question (robot_order_id, question_id,answer_results, question, create_date,user_input,right_answers,interactive_way,duration)
SELECT #{robotOrderId}, #{questionId},#{answerResults},#{question},#{createDate},#{userInput},#{rightAnswers},#{interactiveWay},#{duration}
FROM DUAL WHERE NOT EXISTS(SELECT id FROM ym_robot_question WHERE robot_order_id =#{robotOrderId} AND question_id=#{questionId});
