mapper.update:
UPDATE ym_bpm_node


		node_status = #{node_status,jdbcType=VARCHAR},


		description = #{description,jdbcType=VARCHAR}


where node_id = 5 AND (case when bpm_id IS NULL then per_id = (SELECT id FROM ym_person where phone=#{phone,jdbcType=VARCHAR})

when bpm_id IS NOT NULL then bpm_id = (SELECT bpm_id FROM ym_person where phone=#{phone,jdbcType=VARCHAR})

end)

UPDATE ym_review


		status = #{status,jdbcType=VARCHAR},


		reason = #{reason,jdbcType=VARCHAR},


		employ_num = #{employNum,jdbcType=VARCHAR}


WHERE borr_id=#{borrId,jdbcType=INTEGER}
